	<script type="text/javascript" src="js/jquery-1.11.3.min.js"></script>
	<script type="text/javascript">

		// Sticky
		var stickyTop = $('.sticky').offset().top,
			headerHeight = $('.navbar-default').height() + 10;
		$(window).scroll(function (){
			var	WindowTop = $(window).scrollTop(),
				scrollMain = WindowTop - stickyTop + headerHeight;
				
			if (WindowTop > stickyTop) {
				$('.sticky').css({ 'top': scrollMain });
			} else {
				$('.sticky').css({ 'top': 0});
			}
		});

		// Scrolling
		$(document).on('click', '.default-sidebar a',function(e) {
			e.preventDefault();
			var scrollToLink = $(this).data('id'),
				scrollToTarget = $(scrollToLink).offset().top - $(scrollToLink).closest('[class^="col-"]').offset().top;
			$(this).closest('ul').find('a').removeClass('active');
			$(this).addClass('active');
			$('body').animate({
				scrollTop: scrollToTarget
			});
		});

	</script>

